import classes from './Checkout.module.css'
import { useRef, useState } from 'react';

const isEmpty = value => value.trim() === '';
const isFive = value => value.trim().length !== 5;

const Checkout = props => {
    const [inputvalidity, setInputValidity] = useState({
        name: true,
        street: true,
        city: true,
        pin: true

    });

    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const pinInputRef = useRef();
    const cityInputRef = useRef();

    const confirmh = (event) => {
        event.preventDefault();

        const enteredName = nameInputRef.current.value;
        const enteredStreet = streetInputRef.current.value;
        const enteredPin = pinInputRef.current.value;
        const enteredCity = cityInputRef.current.value;

        const enteredNameIsValid = !isEmpty(enteredName);
        const enteredStreetIsValid = !isEmpty(enteredStreet);
        const enteredCityIsValid = !isEmpty(enteredCity);
        const enteredPinIsValid = !isFive(enteredPin);

        setInputValidity({
            name: enteredNameIsValid,
            street: enteredStreetIsValid,
            city: enteredCityIsValid,
            pin: enteredPinIsValid
        });

        const formIsValid = enteredNameIsValid && enteredPinIsValid && enteredStreetIsValid && enteredCityIsValid;


        if (formIsValid) {
            return;
        }

        props.onConfirm({
            name: enteredName,
            street: enteredStreet,
            city: enteredCity,
            pin: enteredPin,
        });
    };

    const namectrlClasses = `${classes.control} ${inputvalidity.name ? '' : classes.invalid}`;
    const streetctrlClasses = `${classes.control} ${inputvalidity.street ? '' : classes.invalid}`;
    const cityctrlClasses = `${classes.control} ${inputvalidity.city ? '' : classes.invalid}`;
    const pinctrlClasses = `${classes.control} ${inputvalidity.pin ? '' : classes.invalid}`;

    return <form className={classes.form} onSubmit={confirmh}>
        <div className={namectrlClasses}>
            <label htmlFor='name'>Name</label>
            <input type='text' id='name' ref={nameInputRef} />
            {!inputvalidity.name && <p>Enter a valid Name</p>}
        </div>

        <div className={streetctrlClasses}>
            <label htmlFor='street'>Street</label>
            <input type='text' id='street' ref={streetInputRef} />
            {!inputvalidity.name && <p>Enter a valid Street</p>}
        </div>

        <div className={pinctrlClasses}>
            <label htmlFor='pin'>Pin</label>
            <input type='text' id='pin' ref={pinInputRef} />
            {!inputvalidity.name && <p>Five characters min needed</p>}
        </div>

        <div className={cityctrlClasses}>
            <label htmlFor='city'>City</label>
            <input type='text' id='city' ref={cityInputRef} />
        </div>
        <button type='button' className={classes.actions} onClick={props.onCancel}>Cancel</button>
        <button className={classes.button}>Confirm</button>
    </form >
};

export default Checkout;
